mapper.select:
SELECT * FROM PRODUCT
WHERE PRODUCT_NAME LIKE CONCAT('%',#{keyword},'%')
ORDER BY PRODUCT_ID DESC LIMIT #{pageReset}, #{pageAmount}

mapper.update:
UPDATE PRODUCT SET
PRODUCT_NAME=#{productName}, BRAND_ID=#{brandID}, PRODUCT_CATE=#{productCate}, PRODUCT_CODE=#{productCode}, PRODUCT_DESC=#{productDesc}
WHERE PRODUCT_ID = #{productID}
